mapper.update:
update member 
set salt = #{salt, jdbcType=VARCHAR}, userpw = #{userpw, jdbcType=VARCHAR}, name = #{name, jdbcType=VARCHAR}, 
email = #{email, jdbcType=VARCHAR}, gender = #{gender}, profile = #{profile, jdbcType=VARCHAR}, 
phone = #{phone, jdbcType=VARCHAR}, social = #{social, jdbcType=VARCHAR}    
where userid = #{userid}

update member 
set salt = #{salt}, userpw = #{userpw} 
where userid = #{userid}
